import request from "@/utils/request";
import store from "@/store";
//注册接口
export const registerAPI = ({ username, password, repassword }) => {
  return request({
    url: "/api/reg",
    method: "post",
    data: {
      username,
      password,
      repassword,
    },
  });
};

//登陆接口
export const loginAPI = ({ username, password }) => {
  return request({
    url: "/api/login",
    method: "post",
    data: {
      username,
      password,
    },
  });
};

//请求用户信息数据接口
export const getUserInfoAPI = () => {
  return request({
    url: "/my/userinfo",
    headers: {
      Authorization: store.state.token,
    },
  });
};

//获取-侧边栏菜单数据
export const getMenusAPI = () => {
  return request({
    url: "/my/menus",
    headers: {
      Authorization: store.state.token,
    },
  });
};

//更新-用户基本资料
export const updateUserInfoAPI = ({
  id,
  email,
  nickname,
  user_pic,
  username,
}) => {
  return request({
    url: "/my/userinfo",
    method: "PUT",
    data: {
      id,
      email,
      nickname,
      user_pic,
      username,
    },
  });
};

//更新-用户头像
export const updateAvatarAPI = (avatar) => {
  return request({
    url: "/my/update/avatar",
    method: "PATCH",
    data: {
      avatar,
    },
  });
};

//更新-用户密码
export const updatePwdAPI = ({ old_pwd, new_pwd, re_pwd }) => {
  return request({
    url: "/my/updatepwd",
    method: "PATCH",
    data: {
      old_pwd,
      new_pwd,
      re_pwd,
    },
  });
};

//获取-文章分类
export const getArtCateListAPI = () => {
  return request({
    url: "/my/cate/list",
    method: "get",
  });
};

// 增加-文章分类
export const addArtCateAPI = ({ cate_name, cate_alias }) => {
  return request({
    url: "/my/cate/add",
    method: "POST",
    data: {
      cate_name,
      cate_alias,
    },
  });
};

//更新-文章分类
export const updateArtCateAPI = ({ id, cate_name, cate_alias }) => {
  return request({
    url: "/my/cate/info",
    method: "PUT",
    data: {
      id,
      cate_name,
      cate_alias,
    },
  });
};

//删除-文章分类
export const delArtCateAPI = (id) => {
  return request({
    url: "/my/cate/del",
    method: "DELETE",
    params: {
      id,
    },
  });
};

//发布文章
export const uploadArticleAPI = (fd) => {
  return request({
    url: "/my/article/add",
    method: "POST",
    data: fd, // 参数要的是表单对象, 不能写普通对象, axios内部会判断, 如果是表单对象, 传递的请求体会设置Content-Type: form-data与后端对应
  });
};

/** 获取文章列表
 * @param {*} param0 { pagenum: 当前页码数, pagesize: 当前页条数, cate_id: 文章分类id, state: 文章状态 }
 * @returns Promise对象
 */
export const getArticleListAPI = ({ pagenum, pagesize, cate_id, state }) => {
  return request({
    url: "/my/article/list",
    params: {
      pagenum,
      pagesize,
      cate_id,
      state,
    },
  });
};

/** 获取-文章详情
 * @param {*} id 文章id
 * @returns Promise对象
 */
export const getArticleDetailAPI = (id) => {
  return request({
    url: "/my/article/info",
    params: {
      id,
    },
  });
};
